import subprocess
import PySimpleGUI as sg


def gui():
    sg.theme('Black')
    layout = [
        [sg.Text('Hours: ')],
        [sg.Input(key='hours')],
        [sg.Text('Minutes: ')],
        [sg.Input(key='minutes')],
        [sg.Radio('Shutdown', 'Type', key='shutdown', default=True),
         sg.Radio('Restart', 'Type', key='restart'),
         sg.Button('Info', key='Info')],
        [sg.Button('Do it', key='finish'), sg.Button('Abort last', key='abort'), sg.Button('Exit')],

    ]

    window = sg.Window('Shutdowner', layout, icon=icon_bytes)
    while True:
        event, values = window.read()

        if event in (None, "Exit"):
            break
        elif event == 'Info':
            sg.Popup('Ｓｈｕｔｄｏｗｎ： Turns off the computer completely.\n'
                     'Ｒｅｓｔａｒｔ： Shuts down and then restarts the computer. \n',
                     title='Info', icon=icon_bytes )

        elif event == 'finish':
            total_time = 0
            shutdown_command = "shutdown "
            hours = values['hours']
            minutes = values['minutes']
            if minutes == '':
                minutes = 0
            if hours == '':
                hours = 0
            try:
                hours = int(hours)
                minutes = int(minutes)
            except ValueError:
                sg.Popup('Please only use integers in the hours and minutes fields'
                         ' (numbers equal or above 0)', title='Error', icon=icon_bytes)
            total_time += (hours * 3600)
            total_time += (minutes * 60)
            if values['shutdown']:
                shutdown_command += "-s " + "-t " + str(total_time)
            if values['restart']:
                shutdown_command += "-r " + "-t " + str(total_time)
            try:
                subprocess.run(shutdown_command, check=True)
                sg.Popup("Shutdown / restart successfully scheduled for " + str(total_time / 60) + " minutes from now", title="Success", icon=icon_bytes)
            except subprocess.CalledProcessError:
                sg.Popup("You already have a shutdown / restart scheduled (Use the abort button to cancel it) or something went wrong", title="Error", icon=icon_bytes)

        elif event == 'abort':
            try:
                subprocess.run("shutdown /a", check=True)
                sg.Popup("Shutdown / restart aborted successfully", title="Success", icon=icon_bytes)
            except subprocess.CalledProcessError:
                sg.Popup("No shutdown / restart is scheduled", title="Error", icon=icon_bytes)
    window.close()


if __name__ == '__main__':
    icon_bytes = b''
    gui()
